import React, {useEffect, useState} from "react";
import {Column} from "../../../components/column/Column";
import {DataTable} from "../../../components/datatable/DataTable";
import {Dropdown} from "../../../components/dropdown/Dropdown";
import UserDetailsModal from "./userDetailsModal";

export const UsersList = ({data,onSelectUser}) =>{
    const [selected, setSelected] = useState(null);
    const [paginator,setPaginator]=useState({
        first: 0,
        row: 10
    })
    const [detailsDialog,setDetailsDialog] = useState(false)

    useEffect(()=>{
        onSelectUser(selected);
    },[selected])
    const template2 = {
        layout: 'RowsPerPageDropdown CurrentPageReport PrevPageLink NextPageLink',
        'RowsPerPageDropdown': (options) => {
            const dropdownOptions = [
                { label: 10, value: 10 },
                { label: 20, value: 20 },
                { label: 50, value: 50 }
            ];

            return (
                <>
                    <span className="p-mx-1" style={{ color: 'var(--text-color)', userSelect: 'none' }}>Items per page: </span>
                    <Dropdown value={options.value} options={dropdownOptions} onChange={options.onChange} appendTo={document.body} />
                </>
            );
        },
        'CurrentPageReport': (options) => {
            return (
                <span style={{ color: 'var(--text-color)', userSelect: 'none', width: '120px', textAlign: 'center' }}>
                        {options.first} - {options.last} of {options.totalRecords}
                    </span>
            )
        }
    };
    const dat = [
        { id:'123', fname:'gia', lname:'pagava', pid:'12312312312', },
        { id:'1234', fname:'giorgi', lname:'palava', pid:'12312312312', },
        { id:'1235', fname:'xvicha', lname:'chitovichi', pid:'12312312312', },
        { id:'1236', fname:'gia', lname:'trakovichi', pid:'12312312312', },
        { id:'1237', fname:'leo', lname:'chitovichi', pid:'12312312312', },
        { id:'1238', fname:'joni', lname:'chitovichi', pid:'12312312312', },
        { id:'1239', fname:'mirza', lname:'chitovichi', pid:'12312312312', },
    ]
    return (
        data.length>0?
           <>
               <DataTable
                   scrollable
                   paginator
                   paginatorTemplate={template2}
                   onSelectionChange={e => setSelected(e.value)}
                   dataKey="id"
                   onRowSelect={(e)=>console.log("selection",e)} onRowUnselect={(e)=>console.log("unselection",e)}
                   selection={selected}
                   selectionMode="single"
                   value={data}
                   resizableColumns
                   columnResizeMode="fit"
                   first={paginator.first}
                   rows={paginator.row}
                   onPage={(event)=>{
                       setPaginator({
                           ...paginator,
                           first: event.first,
                           row:event.rows
                       })
                   }}
                   paginatorClassName="p-jc-end" className="p-mt-6"
               >
                   <Column field="id" header="#"  style={{width:'50px'}}/>
                   <Column field="fname" header="სახელი"  style={{width:'10%'}}/>
                   <Column field="lname" header="გვარი"  style={{width:'10%'}}/>
                   <Column field="pid" header="პ/ნ / საიდენტიფიკაციო"  style={{width:'10%'}}/>
                   <Column field="photo" header="ფოტოსურათი"  style={{width:'10%'}}/>
                 />
               </DataTable>
               {detailsDialog && <UserDetailsModal user={selected} onClose={setDetailsDialog}/>}
           </> :
            <>
                <DataTable
                    paginator
                    paginatorTemplate={template2}
                    onSelectionChange={e => setSelected(e.value)}
                    selection={selected}
                    dataKey="id"
                    onRowSelect={(e)=>console.log("selection",e)} onRowUnselect={(e)=>console.log("unselection",e)}

                    selectionMode="single"
                    value={dat}
                    resizableColumns
                    columnResizeMode="fit"
                    first={paginator.first}
                    rows={paginator.row}
                    onPage={(event)=>{
                        setPaginator({
                            ...paginator,
                            first: event.first,
                            row:event.rows
                        })
                    }}
                    paginatorClassName="p-jc-end" className="p-mt-6"
                >
                    <Column field="id" header="#"  style={{width:'50px'}}/>
                    <Column field="fname" header="სახელი"  style={{width:'10%'}}/>
                    <Column field="lname" header="გვარი"  style={{width:'10%'}}/>
                    <Column field="pid" header="პ/ნ / საიდენტიფიკაციო"  style={{width:'10%'}}/>
                    <Column field="photo" header="ფოტოსურათი"  style={{width:'10%'}}/>
                </DataTable>

            </>

    )
}
