                    element.style.left = scrollTop + 'px';
                    this.count.y = 0;
                }
                else if (top < 0) {
                    this.count.y++;

                    position.my.y = 'top';
                    position.at.y = 'bottom';
                    position.my.offsetY *= -1;
                    position.at.offsetY *= -1;

