                    this.count.y = 0;
                }
                else if (top + DomHandler.getOuterHeight(target) > viewport.height) {
                    this.count.y++;

                    position.my.y = 'bottom';
                    position.at.y = 'top';
                    position.my.offsetY *= -1;
                    position.at.offsetY *= -1;

