import React, {useEffect, useState} from "react";
import {Column} from "../../../components/column/Column";
import {DataTable} from "../../../components/datatable/DataTable";
import {Dropdown} from "../../../components/dropdown/Dropdown";

export const UsersList = ({data}) =>{
    const [selected, setSelected] = useState(null);
    const [paginator,setPaginator]=useState({
        first: 0,
        row: 10
    })
    const template2 = {
        layout: 'RowsPerPageDropdown CurrentPageReport PrevPageLink NextPageLink',
        'RowsPerPageDropdown': (options) => {
            const dropdownOptions = [
                { label: 10, value: 10 },
                { label: 20, value: 20 },
                { label: 50, value: 50 }
            ];

            return (
                <>
                    <span className="p-mx-1" style={{ color: 'var(--text-color)', userSelect: 'none' }}>Items per page: </span>
                    <Dropdown value={options.value} options={dropdownOptions} onChange={options.onChange} appendTo={document.body} />
                </>
            );
        },
        'CurrentPageReport': (options) => {
            return (
                <span style={{ color: 'var(--text-color)', userSelect: 'none', width: '120px', textAlign: 'center' }}>
                        {options.first} - {options.last} of {options.totalRecords}
                    </span>
            )
        }
    };
    return (
        data.length>0?<DataTable
            paginator
            paginatorTemplate={template2}
            onSelectionChange={e => setSelected(e.value)}
             dataKey="id"
            onRowSelect={(e)=>console.log("selection",e)} onRowUnselect={(e)=>console.log("unselection",e)}
            selection={selected}
            selectionMode="single"
            value={data}
            resizableColumns
            columnResizeMode="fit"
            first={paginator.first}
            rows={paginator.row}
            onPage={(event)=>{
               setPaginator({
                   ...paginator,
                   first: event.first,
                   row:event.rows
               })
            }}
            paginatorClassName="p-jc-end" className="p-mt-6"
        >
            <Column field="id" header="პინი" style={{width:'5%'}} body={(e)=>
                <span onClick={()=>alert(e.id)}>{e.id}</span>
            } />
            <Column field="username" header="მომხმარებლის სახელი" style={{width:'15%'}} />
            <Column field="userGroup" header="ჯგუფი"  style={{width:'10%'}} body={(e)=>
                <span >{e.userGroup.name}</span>
            }/>
            <Column field="fullname" header="სახელი გვარი" style={{width:'15%'}} body={(e)=>
                <span >{e.firstName + " " +e.lastName}</span>
            }/>
            <Column field="mobile" header="მობილური"  style={{width:'10%'}}/>
            <Column field="email" header="ელფოსტა" style={{width:'15%'}}/>
            <Column field="balance" header="მთავარი ბალანსი" style={{width:'10%'}} />
            <Column field="currency" header="ვალუტა" style={{width:'5%'}} body={(e)=>
                <span >{e.currency.iso3}</span>
            }/>
        </DataTable>:<div>Users Data not found</div>
    )
}
