import React, {useEffect, useState} from "react";
import "./users.scss"
import {SearchUser} from "./components/filter/searchUser";
import {SearchIp} from "./components/filter/ipList";
import {Calendar} from "../../components/calendar/Calendar";
import {Dropdown} from "../../components/dropdown/Dropdown";
import {InputNumber} from "../../components/inputnumber/InputNumber";
import {InputText } from "../../components/inputtext/InputText";
import { FileUpload } from '../../components/fileupload/FileUpload';
import {Button} from "../../components/button/Button";
import {UsersList} from "./components/UsersList";
import {UserTabs} from "./components/UserTabs";
import {Actions, Request} from "../../core";
import {useDispatch} from "react-redux";
import {ProgressSpinner} from "../../components/progressspinner/ProgressSpinner";
import moment from "moment";
import UserFilter from "./components/UserFilter";


const Users = () =>{
    const [fname,setFname]=useState('');
    const [lname,setLname]=useState('');
    const [pid,setPid]=useState('');

    const dispatch = useDispatch()
    const [findUserLoader,setFindUserLoader]=useState(false)
    const [data,setData]=useState([])

    const [currencies,setCurrencies]=useState([]);
    const [statusList,setStatusList]=useState([]);
    const [selectedUser,setSelectedUser]=useState(null);
    const getCurrencies=()=>{
        Actions.Config.Currencies().then(response=>{
            setCurrencies(response.status?response.data.data:[])
        })
    }
    const getStatusList=()=> {
        Actions.Config.UserStatusList().then(response=>{
            setStatusList(response.status?response.data.data:[])
        })
    }
    //useEffect(()=>{
    //    getCurrencies();
    //    getStatusList()
    //},[])

    return (
         <div id="users" className="content-section implementation">
             <div className="flex-box call ">
                 {/*<UserFilter setData={setData}/>*/}
                 <div className="flex-cont bg reg-box">
                     <div className="flex-box row">
                         <div className="p-field">
                             <label htmlFor="fname" className="p-d-block">სახელი</label>
                             <InputText id="fname" aria-describedby="username2-help" className=" p-d-block" />
                             {/*<small id="username2-help" className="p-error p-d-block">Username is not available.</small>*/}
                         </div>
                         <div className="p-field">
                             <label htmlFor="lname" className="p-d-block">გვარი</label>
                             <InputText id="lname" aria-describedby="username2-help" className=" p-d-block" />
                             {/*<small id="username2-help" className="p-error p-d-block">Username is not available.</small>*/}
                         </div>
                         <div className="p-field">
                             <label htmlFor="pid" className="p-d-block">პ/ნ</label>
                             <InputText id="pid" aria-describedby="username2-help" className=" p-d-block" />
                             {/*<small id="username2-help" className="p-error p-d-block">Username is not available.</small>*/}
                         </div>
                         <div className="p-field">
                             <label htmlFor="upload" className="p-d-block">photo 1</label>
                             <FileUpload mode="basic" name="demo[]" url="./upload.php" accept="image/*" maxFileSize={1000000} />
                             {/*<small id="username2-help" className="p-error p-d-block">Username is not available.</small>*/}
                         </div>
                         <div className="p-field">
                             <label htmlFor="upload" className="p-d-block">photo 2</label>
                             <FileUpload mode="basic" name="demo[]" url="./upload.php" accept="image/*" maxFileSize={1000000} />
                             {/*<small id="username2-help" className="p-error p-d-block">Username is not available.</small>*/}
                         </div>
                         <div className="p-field">
                             <label htmlFor="" className="p-d-block">&nbsp;</label>
                             <Button label="დამატება" />
                             {/*<small id="username2-help" className="p-error p-d-block">Username is not available.</small>*/}
                         </div>

                     </div>
                 </div>
                 <div className="flex-cont grid bg">
                     <UsersList data={data} onSelectUser={event=>setSelectedUser(event)}/>
                 </div>
             </div>

             <Dialog visible={productDialog} style={{ width: '450px' }} header="Product Details" modal className="p-fluid" footer={productDialogFooter} onHide={hideDialog}>
                 {product.image && <img src={`showcase/demo/images/product/${product.image}`} onError={(e) => e.target.src='https://www.primefaces.org/wp-content/uploads/2020/05/placeholder.png'} alt={product.image} className="product-image" />}
                 <div className="p-field">
                     <label htmlFor="name">Name</label>
                     <InputText id="name" value={product.name} onChange={(e) => onInputChange(e, 'name')} required autoFocus className={classNames({ 'p-invalid': submitted && !product.name })} />
                     {submitted && !product.name && <small className="p-error">Name is required.</small>}
                 </div>
                 <div className="p-field">
                     <label htmlFor="description">Description</label>
                     <InputTextarea id="description" value={product.description} onChange={(e) => onInputChange(e, 'description')} required rows={3} cols={20} />
                 </div>

                 <div className="p-field">
                     <label className="p-mb-3">Category</label>
                     <div className="p-formgrid p-grid">
                         <div className="p-field-radiobutton p-col-6">
                             <RadioButton inputId="category1" name="category" value="Accessories" onChange={onCategoryChange} checked={product.category === 'Accessories'} />
                             <label htmlFor="category1">Accessories</label>
                         </div>
                         <div className="p-field-radiobutton p-col-6">
                             <RadioButton inputId="category2" name="category" value="Clothing" onChange={onCategoryChange} checked={product.category === 'Clothing'} />
                             <label htmlFor="category2">Clothing</label>
                         </div>
                         <div className="p-field-radiobutton p-col-6">
                             <RadioButton inputId="category3" name="category" value="Electronics" onChange={onCategoryChange} checked={product.category === 'Electronics'} />
                             <label htmlFor="category3">Electronics</label>
                         </div>
                         <div className="p-field-radiobutton p-col-6">
                             <RadioButton inputId="category4" name="category" value="Fitness" onChange={onCategoryChange} checked={product.category === 'Fitness'} />
                             <label htmlFor="category4">Fitness</label>
                         </div>
                     </div>
                 </div>

                 <div className="p-formgrid p-grid">
                     <div className="p-field p-col">
                         <label htmlFor="price">Price</label>
                         <InputNumber id="price" value={product.price} onValueChange={(e) => onInputNumberChange(e, 'price')} mode="currency" currency="USD" locale="en-US" />
                     </div>
                     <div className="p-field p-col">
                         <label htmlFor="quantity">Quantity</label>
                         <InputNumber id="quantity" value={product.quantity} onValueChange={(e) => onInputNumberChange(e, 'quantity')} integeronly />
                     </div>
                 </div>
             </Dialog>

             <Dialog visible={deleteProductDialog} style={{ width: '450px' }} header="Confirm" modal footer={deleteProductDialogFooter} onHide={hideDeleteProductDialog}>
                 <div className="confirmation-content">
                     <i className="pi pi-exclamation-triangle p-mr-3" style={{ fontSize: '2rem'}} />
                     {product && <span>Are you sure you want to delete <b>{product.name}</b>?</span>}
                 </div>
             </Dialog>

             <Dialog visible={deleteProductsDialog} style={{ width: '450px' }} header="Confirm" modal footer={deleteProductsDialogFooter} onHide={hideDeleteProductsDialog}>
                 <div className="confirmation-content">
                     <i className="pi pi-exclamation-triangle p-mr-3" style={{ fontSize: '2rem'}} />
                     {product && <span>Are you sure you want to delete the selected products?</span>}
                 </div>
             </Dialog>
             
        </div>
    )

}
export default Users;
