import React, {useEffect, useState} from "react";
import "./users.scss"
import {SearchUser} from "./components/filter/searchUser";
import {SearchIp} from "./components/filter/ipList";
import {Calendar} from "../../components/calendar/Calendar";
import {Dropdown} from "../../components/dropdown/Dropdown";
import {InputNumber} from "../../components/inputnumber/InputNumber";
import {InputText } from "../../components/inputtext/InputText";
import { FileUpload } from '../../components/fileupload/FileUpload';
import { Dialog  } from '../../components/dialog/Dialog';

import {Button} from "../../components/button/Button";
import {UsersList} from "./components/UsersList";
import {UserTabs} from "./components/UserTabs";
import {Actions, Request} from "../../core";
import {useDispatch} from "react-redux";
import {ProgressSpinner} from "../../components/progressspinner/ProgressSpinner";
import moment from "moment";
import UserFilter from "./components/UserFilter";


const Users = () =>{
    const [fname,setFname]=useState('');
    const [lname,setLname]=useState('');
    const [pid,setPid]=useState('');

    const dispatch = useDispatch()
    const [findUserLoader,setFindUserLoader]=useState(false)
    const [data,setData]=useState([])

    const [currencies,setCurrencies]=useState([]);
    const [statusList,setStatusList]=useState([]);
    const [selectedUser,setSelectedUser]=useState(null);
    const getCurrencies=()=>{
        Actions.Config.Currencies().then(response=>{
            setCurrencies(response.status?response.data.data:[])
        })
    }
    const getStatusList=()=> {
        Actions.Config.UserStatusList().then(response=>{
            setStatusList(response.status?response.data.data:[])
        })
    }

    //const [submitted, setSubmitted] = useState(false);
    //const [submitted, setSubmitted] = useState(false);
    //const hideDialog = () => {
    //    setSubmitted(false);
    //    setProductDialog(false);
    //}

    //useEffect(()=>{
    //    getCurrencies();
    //    getStatusList()
    //},[])


    const openNew = () => {
        setProduct(emptyProduct);
        setSubmitted(false);
        setProductDialog(true);
    }

    const hideDialog = () => {
        setSubmitted(false);
        setProductDialog(false);
    }

    const hideDeleteProductDialog = () => {
        setDeleteProductDialog(false);
    }

    const hideDeleteProductsDialog = () => {
        setDeleteProductsDialog(false);
    }

    const saveProduct = () => {
        setSubmitted(true);

        if (product.name.trim()) {
            let _products = [...products];
            let _product = {...product};
            if (product.id) {
                const index = findIndexById(product.id);

                _products[index] = _product;
                toast.current.show({ severity: 'success', summary: 'Successful', detail: 'Product Updated', life: 3000 });
            }
            else {
                _product.id = createId();
                _product.image = 'product-placeholder.svg';
                _products.push(_product);
                toast.current.show({ severity: 'success', summary: 'Successful', detail: 'Product Created', life: 3000 });
            }

            setProducts(_products);
            setProductDialog(false);
            setProduct(emptyProduct);
        }
    }

    const editProduct = (product) => {
        setProduct({...product});
        setProductDialog(true);
    }

    const confirmDeleteProduct = (product) => {
        setProduct(product);
        setDeleteProductDialog(true);
    }

    const deleteProduct = () => {
        let _products = products.filter(val => val.id !== product.id);
        setProduct(_products);
        setDeleteProductDialog(false);
        setProduct(emptyProduct);
        toast.current.show({ severity: 'success', summary: 'Successful', detail: 'Product Deleted', life: 3000 });
    }

    const findIndexById = (id) => {
        let index = -1;
        for (let i = 0; i < products.length; i++) {
            if (products[i].id === id) {
                index = i;
                break;
            }
        }

        return index;
    }

    const createId = () => {
        let id = '';
        let chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
        for (let i = 0; i < 5; i++) {
            id += chars.charAt(Math.floor(Math.random() * chars.length));
        }
        return id;
    }

